def get_booking_text(
    text_key: str,
    fallback_from_db: Optional[str] = None,
    children_count: int = 0,
    **kwargs: Any
) -> str:
    """
    –ü–æ–ª—É—á–∏—Ç—å —Ç–µ–∫—Å—Ç –¥–ª—è –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è —Å —Ñ–æ–ª–ª–±–µ–∫–æ–º –∏–∑ –ë–î/—Å–ª–æ–≤–∞—Ä—è
    –∏ –ø–æ—Å—Ç-–æ–±—Ä–∞–±–æ—Ç–∫–æ–π children_count=0
    
    Args:
        text_key: –ö–ª—é—á —Ç–µ–∫—Å—Ç–∞ –≤ —Å–ª–æ–≤–∞—Ä–µ
        fallback_from_db: –¢–µ–∫—Å—Ç –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö (–ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç)
        children_count: –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–µ—Ç–µ–π
        **kwargs: –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –¥–ª—è —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
        
    Returns:
        –û—Ç—Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç
    """
    # –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç: –ë–î > —Å–ª–æ–≤–∞—Ä—å > –¥–µ—Ñ–æ–ª—Ç
    text = fallback_from_db or TEXTS.get(text_key, TEXTS.get("error", "–¢–µ–∫—Å—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω"))
    
    # –ü–æ—Å—Ç-–æ–±—Ä–∞–±–æ—Ç–∫–∞ –¥–ª—è children_count=0
    if children_count == 0:
        text = text.replace("{children_count}", "0")
        text = text.replace("–¥–µ—Ç–µ–π", "–¥–µ—Ç–µ–π")
    
    # –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
    if kwargs:
        try:
            text = text.format(**kwargs)
        except KeyError:
            pass
    
    return text


